    Rectangle2D rect3 = new Rectangle2D.Double(0, 0, 5, 5);
    RectangularTextContainer chunk3 = new RectangularTextContainer(rect3);
    chunk3.setText(" + more");
    table.add(chunk3, 0, 0);

    // The cell at (0,0) should now contain merged text "Cell 1 + more"
