@Test
public void testGroupByLines() {
    // Prepare TextElements to create TextChunks
    TextElement te1 = new TextElement("Hello", 0, 0, 50, 10);
    TextElement te2 = new TextElement("World", 0, 55, 50, 10);
    TextElement te3 = new TextElement("-----", 20, 0, 100, 10); // repeated char line
    TextElement te4 = new TextElement("Java", 15, 0, 50, 10);
    TextElement te5 = new TextElement("Test", 15, 55, 50, 10);

    // Create TextChunks
    TextChunk chunk1 = new TextChunk(te1);
    TextChunk chunk2 = new TextChunk(te2);
    TextChunk chunk3 = new TextChunk(te3);
    TextChunk chunk4 = new TextChunk(te4);
    TextChunk chunk5 = new TextChunk(te5);

    // Group chunks in a list simulating vertical positions:
    // chunk1 and chunk2 overlap vertically (top=0)
    // chunk3 is a repeated char line (top=20)
    // chunk4 and chunk5 overlap vertically (top=15)
    List<TextChunk> chunks = new ArrayList<>();
    chunks.add(chunk1);
    chunks.add(chunk2);
    chunks.add(chunk3);
    chunks.add(chunk4);
    chunks.add(chunk5);

    // Call the method under test
    List<Line> lines = TextChunk.groupByLines(chunks);

    // Assertions:
    // The repeated char line (chunk3) should be filtered out
    // So lines size should be 2: one for chunk1+chunk2, one for chunk4+chunk5
    org.junit.Assert.assertEquals(2, lines.size());

    // Check first line contains chunk1 and chunk2
    Line firstLine = lines.get(0);
    List<TextChunk> firstLineChunks = firstLine.getTextChunks();
    org.junit.Assert.assertTrue(firstLineChunks.contains(chunk1));
    org.junit.Assert.assertTrue(firstLineChunks.contains(chunk2));

    // Check second line contains chunk4 and chunk5
    Line secondLine = lines.get(1);
    List<TextChunk> secondLineChunks = secondLine.getTextChunks();
    org.junit.Assert.assertTrue(secondLineChunks.contains(chunk4));
    org.junit.Assert.assertTrue(secondLineChunks.contains(chunk5));

    // Check that no line contains the repeated char chunk3
    for (Line line : lines) {
        for (TextChunk tc : line.getTextChunks()) {
            org.junit.Assert.assertNotEquals(chunk3, tc);
        }
    }
}
