    @Test
    public void testToString() {
        float top = 10f;
        float left = 20f;
        float width = 30f;
        float height = 40f;
        Rectangle rect = new Rectangle(top, left, width, height);
        String str = rect.toString();

        float expectedBottom = top + height;
        float expectedRight = left + width;
        String expectedSuffix = String.format(",bottom=%f,right=%f]", expectedBottom, expectedRight);

        assertTrue("toString should end with bottom and right coordinates",
                str.endsWith(expectedSuffix));

        int lastBracketIndex = str.lastIndexOf(']');
        assertTrue("toString should contain '[' before the suffix", lastBracketIndex > 0);

        // Extract prefix by removing the suffix from the end of the string
        String prefix = str.substring(0, str.length() - expectedSuffix.length());

        // Verify that prefix contains expected original coordinates (top and left)
        assertTrue("toString prefix should contain top coordinate", prefix.contains(String.valueOf(top)));
        assertTrue("toString prefix should contain left coordinate", prefix.contains(String.valueOf(left)));
        assertTrue("toString prefix should contain width coordinate", prefix.contains(String.valueOf(width)));
        assertTrue("toString prefix should contain height coordinate", prefix.contains(String.valueOf(height)));
    }
