    @Test
    public void escape_shouldReplaceControlCharactersAndUnicodeSeparators_andRespectHtmlSafeConfig() throws IOException {
        // Arrange:
        // Prepare a mock ZapposJson instance
        ZapposJson zapposJson = mock(ZapposJson.class);

        // Input string containing:
        // - '\b' (backspace, ASCII 8) replaced in REPLACEMENT_CHARS and HTML_SAFE_REPLACEMENT_CHARS as "\\b"
        // - '&' (ASCII 38) replaced only in HTML_SAFE_REPLACEMENT_CHARS as "\\u0026"
        // - Unicode line separator '\u2028' replaced as "\\u2028"
        // - Unicode paragraph separator '\u2029' replaced as "\\u2029"
        // - Normal characters 'a' and 'z' which should remain unchanged
        String input = "a\b&\u2028\u2029z";

        // StringWriter to capture output
        StringWriter writer = new StringWriter();

        // Setup mock to return false for WRITE_HTML_SAFE (use REPLACEMENT_CHARS)
        when(zapposJson.is(WriterConfig.WRITE_HTML_SAFE)).thenReturn(false);

        // Act:
        JsonUtils.escape(zapposJson, input, writer);

        // Assert:
        // Expected output:
        // '\b' replaced by "\\b"
        // '&' not replaced (null in REPLACEMENT_CHARS)
        // '\u2028' replaced by "\\u2028"
        // '\u2029' replaced by "\\u2029"
        // So output: a\b&\u2028\u2029z -> "a\\b&\\u2028\\u2029z"
        String expectedOutputNonHtmlSafe = "a\\b&\\u2028\\u2029z";
        assertEquals(expectedOutputNonHtmlSafe, writer.toString());

        // Reset writer for next test
        writer.getBuffer().setLength(0);

        // Setup mock to return true for WRITE_HTML_SAFE (use HTML_SAFE_REPLACEMENT_CHARS)
        when(zapposJson.is(WriterConfig.WRITE_HTML_SAFE)).thenReturn(true);

        // Act:
        JsonUtils.escape(zapposJson, input, writer);

        // Assert:
        // Expected output:
        // '\b' replaced by "\\b"
        // '&' replaced by "\\u0026"
        // '\u2028' replaced by "\\u2028"
        // '\u2029' replaced by "\\u2029"
        // So output: a\b\u0026\u2028\u2029z -> "a\\b\\u0026\\u2028\\u2029z"
        String expectedOutputHtmlSafe = "a\\b\\u0026\\u2028\\u2029z";
        assertEquals(expectedOutputHtmlSafe, writer.toString());
    }
