@Test
public void testIsNumber() {
    // Valid integers
    assert Strings.isNumber("0");
    assert Strings.isNumber("123");
    assert Strings.isNumber("-123");

    // Valid decimals
    assert Strings.isNumber("0.0");
    assert Strings.isNumber("123.456");
    assert Strings.isNumber("-123.456");

    // Invalid: multiple decimals
    assert !Strings.isNumber("123.45.6");

    // Invalid: letters
    assert !Strings.isNumber("12a3");
    assert !Strings.isNumber("a123");

    // Invalid: empty or null
    assert !Strings.isNumber("");
    assert !Strings.isNumber(null);

    // Invalid: only minus sign
    assert !Strings.isNumber("-");

    // Invalid: decimal point only
    assert !Strings.isNumber(".");

    // Invalid: minus sign not at start
    assert !Strings.isNumber("12-3");

    // Valid: single digit decimal
    assert Strings.isNumber("0.1");

    // Invalid: spaces
    assert !Strings.isNumber(" 123");
    assert !Strings.isNumber("123 ");
    assert !Strings.isNumber("1 23");

    // Invalid: plus sign not allowed
    assert !Strings.isNumber("+123");
}
