    @Test
    public void testToString_includesSuperStringAndTextChunksContents() {
        class TextChunk {
            private final String text;
            public TextChunk(String text) {
                this.text = text;
            }
            public String getText() {
                return this.text;
            }
        }

        Line line = new Line();
        // Initialize textChunks as a standard ArrayList to allow add()
        line.textChunks = new ArrayList<>();

        line.textChunks.add(new TextChunk("Hello"));
        line.textChunks.add(new TextChunk("World"));
        line.textChunks.add(new TextChunk("!"));

        String result = line.toString();
        assertTrue(result.contains("'Hello'"));
        assertTrue(result.contains("'World'"));
        assertTrue(result.contains("'!'"));
        assertTrue(result.contains(",chunks="));
        assertTrue(result.endsWith("]"));
    }
