public void testHorizontalOverlap() {
    // Case 1: Partial overlap
    Rectangle r1 = new Rectangle(0, 0, 10, 10); // left=0, right=10
    Rectangle r2 = new Rectangle(0, 5, 10, 10); // left=5, right=15
    float overlap1 = r1.horizontalOverlap(r2);
    // Overlap should be from 5 to 10 = 5
    org.junit.Assert.assertEquals(5.0f, overlap1, 0.0001f);

    // Case 2: Full overlap (r2 inside r1)
    Rectangle r3 = new Rectangle(0, 2, 5, 10); // left=2, right=7
    float overlap2 = r1.horizontalOverlap(r3);
    // Overlap should be width of r3 = 5
    org.junit.Assert.assertEquals(5.0f, overlap2, 0.0001f);

    // Case 3: No overlap (r2 completely to the right)
    Rectangle r4 = new Rectangle(0, 11, 5, 10); // left=11, right=16
    float overlap3 = r1.horizontalOverlap(r4);
    // No overlap, should be 0
    org.junit.Assert.assertEquals(0.0f, overlap3, 0.0001f);

    // Case 4: Touching edges (right edge of r1 == left edge of r4)
    Rectangle r5 = new Rectangle(0, 10, 5, 10); // left=10, right=15
    float overlap4 = r1.horizontalOverlap(r5);
    // Touching edges, overlap length = 0
    org.junit.Assert.assertEquals(0.0f, overlap4, 0.0001f);

    // Case 5: r1 inside r2
    Rectangle r6 = new Rectangle(0, -5, 20, 10); // left=-5, right=15
    float overlap5 = r1.horizontalOverlap(r6);
    // Overlap should be width of r1 = 10
    org.junit.Assert.assertEquals(10.0f, overlap5, 0.0001f);
}
