@Test
public void testSplitAt() {
    // Arrange:
    // Create several TextElement instances to form a TextChunk
    TextElement te1 = new TextElement(0f, 0f, 10f, 10f, "A");
    TextElement te2 = new TextElement(0f, 10f, 10f, 10f, "B");
    TextElement te3 = new TextElement(0f, 20f, 10f, 10f, "C");
    TextElement te4 = new TextElement(0f, 30f, 10f, 10f, "D");
    List<TextElement> elements = List.of(te1, te2, te3, te4);
    TextChunk chunk = new TextChunk(elements);

    // Act:
    // Split at index 2
    TextChunk[] splitChunks = chunk.splitAt(2);

    // Assert:
    // Check that two chunks are returned
    assertNotNull(splitChunks);
    assertEquals(2, splitChunks.length);

    // First chunk should contain te1, te2
    List<TextElement> firstChunkElements = splitChunks[0].getTextElements();
    assertEquals(2, firstChunkElements.size());
    assertSame(te1, firstChunkElements.get(0));
    assertSame(te2, firstChunkElements.get(1));

    // Second chunk should contain te3, te4
    List<TextElement> secondChunkElements = splitChunks[1].getTextElements();
    assertEquals(2, secondChunkElements.size());
    assertSame(te3, secondChunkElements.get(0));
    assertSame(te4, secondChunkElements.get(1));

    // Test that the original chunk remains unchanged size
    assertEquals(4, chunk.getTextElements().size());

    // Test boundary conditions: i == 1 (lowest valid split)
    TextChunk[] splitAtOne = chunk.splitAt(1);
    assertEquals(1, splitAtOne[0].getTextElements().size());
    assertSame(te1, splitAtOne[0].getTextElements().get(0));
    assertEquals(3, splitAtOne[1].getTextElements().size());
    assertSame(te2, splitAtOne[1].getTextElements().get(0));
    assertSame(te3, splitAtOne[1].getTextElements().get(1));
    assertSame(te4, splitAtOne[1].getTextElements().get(2));

    // Test boundary condition: i == size-1 (highest valid split)
    TextChunk[] splitAtThree = chunk.splitAt(3);
    assertEquals(3, splitAtThree[0].getTextElements().size());
    assertSame(te1, splitAtThree[0].getTextElements().get(0));
    assertSame(te2, splitAtThree[0].getTextElements().get(1));
    assertSame(te3, splitAtThree[0].getTextElements().get(2));
    assertEquals(1, splitAtThree[1].getTextElements().size());
    assertSame(te4, splitAtThree[1].getTextElements().get(0));

    // Test invalid split indices: less than 1
    assertThrows(IllegalArgumentException.class, () -> {
        chunk.splitAt(0);
    });

    // Test invalid split indices: greater or equal to size
    assertThrows(IllegalArgumentException.class, () -> {
        chunk.splitAt(4);
    });
}
